<?php
/**
 * The Menu Class.
 *
 * @package WapuuGotchi
 */

namespace Wapuugotchi\Core;

if ( ! defined( 'ABSPATH' ) ) {
	exit();
}

/**
 * Class Menu
 */
class Menu {
	/**
	 * "Constructor" of this Class
	 */
	public function __construct() {
		\add_action( 'admin_menu', array( $this, 'create_menu_page' ) );
	}

	/**
	 * Check if the submenu array is valid.
	 *
	 * @param array $submenu The submenu array.
	 *
	 * @return bool
	 */
	private function is_submenu_array_valid( $submenu ) {
		if ( ! is_array( $submenu ) || empty( $submenu ) ) {
			return false;
		}

		if ( ! isset( $submenu['title'] ) || ! isset( $submenu['callback'] ) || ! isset( $submenu['slug'] ) ) {
			return false;
		}

		if ( ! \is_callable( $submenu['callback'] ) ) {
			return false;
		}

		return true;
	}

	/**
	 * Get validated submenu entries.
	 *
	 * @return array
	 */
	private function get_submenu_entries() {
		$submenus = \apply_filters( 'wapuugotchi_add_submenu', array() );
		if ( ! is_array( $submenus ) ) {
			return array();
		}

		$result = array();
		foreach ( $submenus as $submenu ) {
			if ( false === $this->is_submenu_array_valid( $submenu ) ) {
				continue;
			}

			$result[] = $submenu;
		}

		return $result;
	}

	/**
	 * Add menu to admin sidebar.
	 *
	 * @return void
	 */
	public function create_menu_page() {
		$submenu_entries = $this->get_submenu_entries();

		$slug       = 'wapuugotchi';
		$capability = 'manage_options';
		foreach ( $submenu_entries as $index => $submenu_entry ) {
			if ( 0 === $index ) {
				\add_menu_page(
					__( 'WapuuGotchi', 'wapuugotchi' ),
					__( 'WapuuGotchi', 'wapuugotchi' ),
					$capability,
					$slug,
					$submenu_entry['callback'],
					'data:image/svg+xml;base64,'
				);
			}

			\add_submenu_page(
				$slug,
				__( 'WapuuGotchi', 'wapuugotchi' ),
				$submenu_entry['title'],
				$capability,
				$submenu_entry['slug'],
				$submenu_entry['callback']
			);
		}
	}
}
